class App extends Component {
    constructor(props){
        super(props);

        this.state = {
            clock: 1000,
            copo: '치gua',
        }
    }

    alterarCopo = () => {
        this.setState({
            copo: 'refrigerante'
        })
    } 

    render() {
        const {clock, copo} = this.state;
        return(
            <div>
                <h1>{clock}</h1>
                <button onClick={alterarCopo()}>{copo}</button>
            </div>
        )
    }
}


const rootElement = document.getElementById ("root");
ReactDOM.render(<App />, rootElement);
